CREATE DATABASE LojaCREATE LOGIN loja WITH PASSWORD = 'loja', CHECK_POLICY = OFFCREATE USER loja FOR LOGIN lojaUSE LojaCREATE SEQUENCE listaPessoa AS INTEGER START WITH 1 INCREMENTED BY 1;CREATE TABLE [Pessoa] (  idPessoa int NOT NULL,  Nome varchar(100) NOT NULL,  Endereco varchar(200) NOT NULL,  Cidade varchar(50) NOT NULL,  Estado varchar(50) NOT NULL,  Email varchar(255) NOT NULL,  Telefone varchar(15) NOT NULL  CONSTRAINT PK_Pessoa PRIMARY KEY CLUSTERED (idPessoa))CREATE TABLE [PessoaFisica] (  FK_Pessoa_id int NOT NULL,  CPF varchar(14) NOT NULL,  CONSTRAINT PK_PessoaFisica PRIMARY KEY CLUSTERED (FK_Pessoa_id),  CONSTRAINT FK_Pessoa_Fisica FOREIGN KEY (FK_Pessoa_id) REFERENCES Pessoa(idPessoa)	ON UPDATE CASCADE ON DELETE CASCADE,)CREATE TABLE [PessoaJuridica] (  FK_Pessoa_id int NOT NULL,  CNPJ varchar(18) NOT NULL,  CONSTRAINT PK_PessoaJuridica PRIMARY KEY CLUSTERED (FK_Pessoa_id),  CONSTRAINT FK_Pessoa_Juridica FOREIGN KEY (FK_Pessoa_id) REFERENCES Pessoa(idPessoa)	ON UPDATE CASCADE ON DELETE CASCADE,)CREATE TABLE [Produto] (  idProduto int NOT NULL,  Nome varchar(255) NOT NULL,  Quantidade int NOT NULL,  PrecoVenda decimal(25,2) NOT NULL,  CONSTRAINT PK_Produto PRIMARY KEY CLUSTERED (idProduto))CREATE TABLE [Usuario] (  idUsuario int NOT NULL,  Login varchar(25) NOT NULL,  Senha varchar(25) NOT NULL,  CONSTRAINT PK_Usuario PRIMARY KEY CLUSTERED (idUsuario))CREATE TABLE [Movimento] (  idMovimento int NOT NULL UNIQUE,  FK_Usuario_id int NOT NULL,  FK_Pessoa_id int NOT NULL,  FK_Produto_id int NOT NULL,  Tipo varchar(255) NOT NULL,  Quantidade int NOT NULL,  PrecoUnitario decimal(25,2) NOT NULL,  CONSTRAINT PK_Movimento PRIMARY KEY CLUSTERED (idMovimento),  CONSTRAINT FK_Usuario FOREIGN KEY (FK_Usuario_id) REFERENCES Usuario(idUsuario)	ON UPDATE CASCADE ON DELETE CASCADE,  CONSTRAINT FK_Pessoa FOREIGN KEY (FK_Pessoa_id) REFERENCES Pessoa(idPessoa)	ON UPDATE CASCADE ON DELETE CASCADE,  CONSTRAINT FK_Produto FOREIGN KEY (FK_Produto_id) REFERENCES Produto(idProduto)	ON UPDATE CASCADE ON DELETE CASCADE)